mapper.update:
UPDATE student_answers
SET progress = 0, status = '正常'
WHERE exam_id = #{examId} AND student_id = #{studentId}

UPDATE student_answers
SET progress = @new_progress := progress + 1
WHERE exam_id = #{examId} AND student_id = #{studentId};

SELECT @new_progress AS progress;

UPDATE student_answers
SET progress = @new_progress := progress - 1
WHERE exam_id = #{examId} AND student_id = #{studentId};

SELECT @new_progress AS progress;

UPDATE student_answers
SET is_final = true
WHERE exam_id = #{examId} AND student_id = #{studentId}
